select logement.id_logement, logement.type from  logement where logement.id_logement not in (select logement_attribue.id_logement from logement_attribue INNER join reservation_sejour on logement_attribue.id_sejour=reservation_sejour.id_sejour where (reservation_sejour.date_debut_sejour BETWEEN '2020-10-24' AND '2020-10-31') or (reservation_sejour.date_fin_sejour BETWEEN '2020-10-24' AND '2020-10-31') or (reservation_sejour.date_debut_sejour < '2020-10-24' AND reservation_sejour.date_fin_sejour > '2020-10-31'));


SELECT logement.id_logement, logement.type FROM logement INNER JOIN logement_attribue ON logement.id_logement!=logement_attribue.id_logement INNER JOIN reservation_sejour ON logement_attribue.id_sejour=reservation_sejour.id_sejour WHERE (reservation_sejour.date_debut_sejour BETWEEN '2020-10-24' AND '2020-10-31') OR (reservation_sejour.date_fin_sejour BETWEEN '2020-10-24' AND '2020-10-31') OR (reservation_sejour.date_debut_sejour < '2020-10-24' AND reservation_sejour.date_fin_sejour > '2020-10-31');